function openNav() {
    $(".sidenav").css("width", "250px");
    $(".sidenav").addClass("open");
}

function closeNav() {
    $(".sidenav").css("width", "0");
    $(".sidenav").removeClass("open");
}

$(document).click(function(event) {
    var clickTarget = $(event.target);
    var sideNav = $('.sidenav');

    // Check if the click target is the close button and the side navigation is open
    if (clickTarget.is('.sidenav-close') && sideNav.hasClass('open')) {
        closeNav();
        return;
    }

    // Check if the click target is outside the navbar and the side navigation is open
    if (sideNav.hasClass('open') && !clickTarget.closest('.navbar-toggler').length && !clickTarget.closest('.sidenav').length) {
        closeNav();
        return;
    }

});









